def day1_1():
    start = 0
    for i in day1_input:
        if i == "(":
            start +=1
        elif i == ")":
            start -=1
    return(start)


def day1_2():
    start = 0
    count = 0
    stop = ""
    for i in day1_input:
        count +=1
        if i == "(":
            start +=1
        elif i == ")":
            start -=1
        if start <0 and stop == "":
            stop = count
    return(stop)

day1_1ans = day1_1()
day1_2ans = day1_2()

