def day2_1():
    with open('2015-day2-input.txt') as f:
        lines = f.readlines()
        day2_input = []
        for i in lines:
            day2_input.append(i.strip())
    total = 0
    for i in day2_input:
        total = wrapp(i,total)
    return total

def wrapp(input,total):
    measurements = input.split("x")
    l = int(measurements[0])*int(measurements[1])
    w = int(measurements[1])*int(measurements[2])
    h = int(measurements[2])*int(measurements[0])
    total = total+ (2*(l+w+h)+min(l,w,h))
    return total


day2_1ans = day2_1()


print(f"Day 2 part A - answer: {day2_1ans}")
